mapper.select:
SELECT

FROM t_disk_file a
WHERE a.id = #{id}

SELECT

FROM t_disk_file a

    a.status = #{status}
    AND a.code = #{code}

        AND a.id  <>  #{excludeFileId}

SELECT
FROM t_disk_file a
a.status = #{status}
AND a.folder_id = #{folderId}
AND a.name LIKE
'%'||#{query}||'%' CONCAT('%', #{query}, '%') '%'||#{query}||'%' '%'+#{query}+'%'
ORDER BY ${entityPage.orderBy} ${entityPage.order}
ORDER BY a.update_time DESC

SELECT

FROM t_disk_file a


    a.status = #{status}

        AND a.user_id = #{userId}


        AND a.folder_id = #{folderId}



        AND a.name LIKE

            '%'||#{query}||'%'
            CONCAT('%', #{query}, '%')
            '%'||#{query}||'%'
            '%'+#{query}+'%'





                AND a.file_size  < #{fileSize}


                AND a.file_size  >= #{minSize} AND a.file_size  <= #{maxSize}


                AND a.file_size  > #{fileSize}







        ORDER BY ${entityPage.orderBy} ${entityPage.order}


        ORDER BY a.update_time DESC

SELECT

FROM t_disk_file a


    a.status = #{status}
    AND a.folder_id = #{folderId}

        AND a.file_suffix IN

            #{item}





        ORDER BY ${entityPage.orderBy} ${entityPage.order}


        ORDER BY a.update_time DESC

SELECT
  sum(a.file_size)
FROM t_disk_file a

    a.status = #{status}

        AND a.id IN

            #{item}

mapper.delete:
UPDATE t_disk_file SET
status = #{STATUS_DELETE},
version = version +1,
update_user = #{updateUser},
update_time = #{updateTime}
WHERE id = #{id}
