mapper.update:
UPDATE t_sys_user SET
status = #{status},
version = version +1,
update_user = #{updateUser},
update_time = #{updateTime},
ORIGINAL_PASSWORD = #{originalPassword},
PASSWORD = #{password}



            login_name = #{loginName} AND status = #{status}


            id = #{id}

mapper.delete:
UPDATE t_sys_user SET
status = #{STATUS_DELETE},
version = version +1,
update_user = #{updateUser},
update_time = #{updateTime}
WHERE id = #{id}
