$.afui.useOSThemes=false;var $loginName;var $password;var $rememberMe;$(function(){$loginName=$("#loginName");$password=$("#password");$rememberMe=$("#rememberMe");$rememberMe.prop("checked",window.localStorage.getItem("rememberMe")==""?false:true);$loginName.val(window.localStorage.getItem("loginName"));if(window.localStorage.getItem("rememberMe")){$password.val(window.localStorage.getItem("password"))}else{$loginName.focus()}$rememberMe.click(function(){var a=$(this).prop("checked");if(a){window.localStorage.setItem("loginName",$loginName.val());window.localStorage.setItem("password",$password.val());window.localStorage.setItem("rememberMe",a)}else{window.localStorage.setItem("password","");window.localStorage.setItem("rememberMe","")}})});function login(){if(!$loginName.val()){$.afui.toast({message:"账号不能为空!",position:"tc",autoClose:true,type:"success"});return}if(!$password.val()){$.afui.toast({message:"密码不能为空!",position:"tc",autoClose:true,type:"success"});return}$.ajax({url:ctxAdmin+"/login/login",type:"post",data:{loginName:$loginName.val(),password:$password.val(),checkDevice:false},traditional:true,async:false,dataType:"json",success:function(a){if(a.code==1){if(a.obj){setTimeout(function(){window.location=a.obj},1000)}else{setTimeout(function(){window.location=ctxMobile},1000)}}else{$.afui.toast({message:a.msg,position:"tc",autoClose:true,type:"warning"})}}})};