mapper.select:
SELECT

FROM t_sys_log a

WHERE a.id = #{id}

SELECT
FROM t_sys_log a
a.status = #{STATUS_NORMAL}
AND ( a.title LIKE '%'||#{query}||'%' CONCAT('%', #{query}, '%') '%'||#{query}||'%' OR a.ip LIKE '%'||#{query}||'%' CONCAT('%', #{query}, '%') '%'||#{query}||'%' OR a.remark LIKE '%'||#{query}||'%' CONCAT('%', #{query}, '%') '%'||#{query}||'%' )
AND a.type = #{type}
AND ( u.name LIKE '%'||#{userId}||'%' CONCAT('%', #{userId}, '%') '%'||#{userId}||'%' OR u.login_name LIKE '%'||#{userId}||'%' CONCAT('%', #{userId}, '%') '%'||#{userId}||'%' )
ORDER BY ${entityPage.orderBy} ${entityPage.order}
ORDER BY a.create_time DESC

SELECT

FROM t_sys_log a


	a.status = #{STATUS_NORMAL}



		ORDER BY ${entityPage.orderBy} ${entityPage.order}


		ORDER BY a.create_time DESC

SELECT count(*) FROM t_sys_log a WHERE t.status = '0' AND t.title LIKE '%'||'用户登录'||'%' CONCAT('%', '用户登录', '%') '%'||'用户登录'||'%' AND a.user_id = #{userId}
AND a.create_time BETWEEN #{startTime} AND #{endTime}
AND a.create_time >= #{startTime}
AND a.create_time <= #{endTime}

SELECT o.id AS "organId", o.name AS "department", u.name AS "name", u.login_name AS "userName", count(*) AS "count" FROM t_sys_log t LEFT JOIN t_sys_user u ON u.id = t.user_id LEFT JOIN t_sys_organ o ON o.id = u.default_organ_id WHERE t.status = '0' AND t.title LIKE '%'||'用户登录'||'%' CONCAT('%', '用户登录', '%') '%'||'用户登录'||'%'
AND (u.login_name LIKE '%'||#{name}||'%' CONCAT('%', #{name}, '%') '%'||#{name}||'%' OR u.name LIKE '%'||#{name}||'%' CONCAT('%', #{name}, '%') '%'||#{name}||'%')
AND t.create_time BETWEEN #{startTime} AND #{endTime}
AND t.create_time >= #{startTime}
AND t.create_time <= #{endTime}
GROUP BY o.id,o.name,u.name,u.login_name ORDER BY 5 DESC

SELECT t.TITLE AS "module", count(*) AS "moduleCount" FROM t_sys_log t LEFT JOIN t_sys_user u ON u.id = t.user_id LEFT JOIN t_sys_organ_extend o ON o.id = u.default_organ_id
LEFT JOIN t_sys_user_post up ON up.user_id = u.id LEFT JOIN t_sys_post p ON p.id = up.post_id
WHERE t.status = '0'
AND u.id in
#{objectIds, jdbcType=VARCHAR}
AND o.company_id = #{organId}
AND (o.id = #{organId} OR o.parent_ids LIKE '%'||#{organId}||'%' CONCAT('%', #{organId}, '%') '%'||#{organId}||'%' )
AND t.create_time BETWEEN #{startTime} AND #{endTime}
AND t.create_time >= #{startTime}
AND t.create_time <= #{endTime}
AND p.code = #{postCode}
GROUP BY t.TITLE ORDER BY 2 DESC

SELECT
FROM t_sys_log a
WHERE a.title IS NOT NULL AND a.module = #{module} LIMIT 1;

mapper.update:
UPDATE t_sys_log SET
status = #{STATUS_DELETE}
WHERE id = #{id}
