mapper.select:
SELECT

FROM t_sys_resource a

WHERE a.id = #{id}

SELECT
FROM t_sys_resource a
WHERE a.status = '0' AND a.code = #{code}

SELECT

FROM t_sys_resource a



        AND a.status = #{status}


        AND a.code = #{code}


        AND a.type = #{type}


ORDER BY a.sort ASC

SELECT
FROM t_sys_resource a LEFT JOIN t_sys_user_resource ur0 ON ur0.resource_id = a.id LEFT JOIN t_sys_user u0 ON ur0.user_id = u0.id
a.status = '0' AND u0.id = #{userId}
AND a.type IN
#{item}
ORDER BY a.sort ASC

SELECT a.id FROM t_sys_resource a LEFT JOIN t_sys_user_resource ur0 ON ur0.resource_id = a.id LEFT JOIN t_sys_user u0 ON ur0.user_id = u0.id
a.status = '0' AND u0.id = #{userId}
AND a.type IN
#{item}
ORDER BY a.sort ASC

SELECT
FROM t_sys_resource a LEFT JOIN t_sys_role_resource rr ON rr.resource_id = a.id LEFT JOIN t_sys_role r ON r.id = rr.role_id
a.status = '0' AND r.id = #{roleId}
AND a.type IN
#{item}
ORDER BY a.sort ASC

SELECT a.id FROM t_sys_resource a LEFT JOIN t_sys_role_resource rr ON rr.resource_id = a.id LEFT JOIN t_sys_role r ON r.id = rr.role_id
a.status = '0' AND r.id = #{roleId}
AND a.type IN
#{item}
ORDER BY a.sort ASC

SELECT DISTINCT
FROM t_sys_resource a LEFT JOIN t_sys_user_resource ur0 ON ur0.resource_id = a.id LEFT JOIN t_sys_user u0 ON ur0.user_id = u0.id
a.status = '0' AND u0.id = #{userId}
AND a.type IN
#{item}
OR a.id IN ( SELECT r.id FROM t_sys_user u LEFT JOIN t_sys_user_role ur ON ur.user_id = u.id LEFT JOIN t_sys_role role ON role.id = ur.role_id LEFT JOIN t_sys_role_resource rr ON rr.role_id = role.id LEFT JOIN t_sys_resource r ON r.id = rr.resource_id
r.status = '0' AND role.status = '0' AND u.id = #{userId}
AND r.type IN
#{item}
)
ORDER BY a.sort ASC

mapper.update:
UPDATE t_sys_area SET
parent_id = #{parent.id},
parent_ids = #{parentIds}
WHERE id = #{id}

mapper.delete:
UPDATE t_sys_resource SET
status = #{STATUS_DELETE},
version = version +1,
update_user = #{updateUser},
update_time = #{updateTime}
WHERE id = #{id}
