mapper.select:
SELECT

FROM t_disk_folder a

WHERE a.id = #{id}

SELECT

FROM t_disk_folder a


    a.status = #{status}



        ORDER BY ${entityPage.orderBy} ${entityPage.order}


        ORDER BY a.sort ASC

SELECT

FROM t_disk_folder a


    a.status = #{status}
    AND a.type = #{type}


            AND a.parent_id = #{id}


            AND a.parent_id IS null or a.parent_id == ''



ORDER BY a.sort ASC

SELECT
FROM t_disk_folder a
a.status = #{status}
AND a.type = #{type}
AND (a.parent_id = #{id} OR a.parent_ids LIKE '%,'||#{id}||',%' CONCAT('%,', #{id}, ',%') '%,'||#{id}||',%')
ORDER BY a.sort ASC

SELECT a.id FROM t_disk_folder a
a.status = #{status}
AND a.type = #{type}
AND (a.parent_id = #{id} OR a.parent_ids LIKE '%,'||#{id}||',%' CONCAT('%,', #{id}, ',%') '%,'||#{id}||',%')
ORDER BY a.sort ASC

SELECT

FROM t_disk_folder a


    a.status = #{status}

        AND a.user_id = #{userId}


        AND a.type = #{type}


        AND a.folder_authorize = #{folderAuthorize}


        AND a.code = #{code}


        AND  a.parent_id = #{parentId}


        AND a.id  <>  #{excludeFolderId} AND a.parent_ids NOT LIKE
        '%,'||#{excludeFolderId}||',%'
        CONCAT('%,', #{excludeFolderId}, ',%'
        '%,'||#{excludeFolderId}||',%')



ORDER BY a.sort ASC

SELECT

FROM t_disk_folder a


    a.status = #{status}

        AND a.user_id = #{userId}


      AND a.type = #{type}


        AND a.folder_authorize = #{folderAuthorize}


        AND a.code = #{code}


ORDER BY a.sort ASC

mapper.delete:
UPDATE t_disk_folder SET
status = #{STATUS_DELETE},
version = version +1,
update_user = #{updateUser},
update_time = #{updateTime}
WHERE id = #{id}
