mapper.select:
SELECT 

FROM t_notice_message a

WHERE a.id = #{id}

SELECT
FROM t_notice_message a
a.status = #{status}
AND a.app_id = #{appId}
AND a.organ_id = #{organId}
AND a.category = #{category}
AND ( a.title LIKE '%'||#{query}||'%' CONCAT('%', #{query}, '%') '%'||#{query}||'%' OR a.url LIKE '%'||#{query}||'%' CONCAT('%', #{query}, '%') '%'||#{query}||'%' OR a.content LIKE '%'||#{query}||'%' CONCAT('%', #{query}, '%') '%'||#{query}||'%' OR a.app_id LIKE '%'||#{query}||'%' CONCAT('%', #{query}, '%') '%'||#{query}||'%' )
${sqlMap.dsf}
ORDER BY ${entityPage.orderBy} ${entityPage.order}
ORDER BY a.update_time DESC

SELECT 

FROM t_notice_message a


	a.status = #{STATUS_NORMAL}

		AND a.organ_id = #{organId}




		ORDER BY ${entityPage.orderBy} ${entityPage.order}


		ORDER BY a.update_time DESC

mapper.update:
		UPDATE t_notice_message SET
		status = #{STATUS_DELETE},
		version = version +1,
        update_user = #{updateUser},
        update_time = #{updateTime}
		WHERE id = #{id}
