mapper.select:
SELECT

FROM t_notice a

WHERE a.id = #{id}

SELECT

FROM t_notice a


	a.status = #{STATUS_NORMAL}

		AND (a.title LIKE
		'%'||#{query}||'%'
		CONCAT('%', #{query}, '%')
		'%'||#{query}||'%'
		OR a.content LIKE
		'%'||#{query}||'%'
		CONCAT('%', #{query}, '%')
		'%'||#{query}||'%')


		AND a.type = #{type}


		AND a.biz_mode = #{bizMode}

	${sqlMap.dsf}



		ORDER BY ${entityPage.orderBy} ${entityPage.order}


		ORDER BY a.update_time DESC

SELECT

FROM t_notice a


	a.status = #{STATUS_NORMAL}



		ORDER BY ${entityPage.orderBy} ${entityPage.order}


		ORDER BY a.update_time DESC

SELECT

FROM t_notice a


	a.status = #{status}

		AND a.user_id = #{userId}


		AND a.title LIKE
		'%'||#{title}||'%'
		CONCAT('%', #{title}, '%')
		'%'||#{title}||'%'


		AND a.content LIKE
		'%'||#{content}||'%'
		CONCAT('%', #{content}, '%')
		'%'||#{content}||'%'


		AND a.is_top = #{isTop}


		AND a.biz_mode = #{bizMode}




			AND a.publish_time BETWEEN #{startTime} AND #{endTime}


			AND a.publish_time >= #{startTime}


			AND a.publish_time <= #{endTime}


	${sqlMap.dsf}



		ORDER BY ${entityPage.orderBy} ${entityPage.order}


		ORDER BY a.update_time DESC

mapper.update:
		UPDATE t_notice SET
		status = #{STATUS_DELETE},
		version = version +1,
        update_user = #{updateUser},
        update_time = #{updateTime}
		WHERE id = #{id}

mapper.select:
SELECT a.file_id
FROM t_notice_file a
WHERE a.notice_id = #{noticeId}

mapper.delete:
DELETE FROM t_notice_file


		AND notice_id = #{id}


