mapper.select:
SELECT

FROM t_sys_role a
WHERE a.id = #{id}

SELECT

FROM t_sys_role a
WHERE a.status = '0' AND a.code = #{code}

SELECT

FROM t_sys_role a

    a.status = #{status}

        AND (a.name  LIKE
        '%'||#{query}||'%'
        CONCAT('%', #{query}, '%')
        OR a.code  = #{query}
        )


        AND a.organ_id = #{organId}




        ORDER BY ${entityPage.orderBy} ${entityPage.order}


        ORDER BY a.update_time DESC

SELECT

FROM t_sys_role a

    a.id IN #{item}

SELECT
FROM t_sys_role a
a.status = '0' AND a.organ_id = #{organId}

SELECT
FROM t_sys_role a LEFT JOIN t_sys_user_role ur ON ur.role_id = a.id LEFT JOIN t_sys_user u On u.id = ur.user_id
a.status = '0' AND u.id = #{userId}

mapper.delete:
UPDATE t_sys_role SET
status = #{STATUS_DELETE},
version = version +1,
update_user = #{updateUser},
update_time = #{updateTime}
WHERE id = #{id}

mapper.select:
SELECT a.organ_id
FROM t_sys_role_organ a
WHERE a.role_id = #{id}
